class Solution:
    def twoSum(self, nums: List[int], target: int) -> List[int]:
      hashmap = {}
      for i in range(len(nums)):
        complementary = target - nums[i]
        if complementary in hashmap:
          return [hashmap[complementary], i]
        hashmap[nums[i]] = i

#pragma once

const int MAX_SIZE = 1000;
typedef unsigned int uint;

struct PhoneNumber {
	string zipCode;
	string digits;
};

struct Date {
	uint date;
	uint month;
	uint year;
};

struct Subscription {
	uint id;
	string fullName;
	string address;
	PhoneNumber phoneNumber;
	Date contractDate;
};

typedef Subscription Subscriptions[MAX_SIZE];
